//JSONLoader Class
export class JSONLoader{

    /**
     * load JSON data and load it into whatever format specified in the data
     * @param {string} url the url to a json file
     * @param {Loader} loader the main loader
     */
    load(url,loader){
        let request = new XMLHttpRequest();
        request.overrideMimeType("application/json");
        request.open("GET",url,true);
        request.onload = function(){
            let data = JSON.parse(request.response);
            console.log(data);
            if(data.tiledversion != null){
                let map = new TiledMap(url,data);
                console.log(map);
                let success = function(){
                    loader.successCount++;
                    loader.cache[url] = map;
                    if(loader.isDone()){
                        loader.callback();
                    }
                };
                let error = function(){
                    loader.errorCount++;
                    console.error(`Failed to load asset: { url: ${url}}`);
                    if(loader.isDone()){
                        loader.callback();
                    }
                };
                map.setCallbacks(success,error);
            }else{
                loader.successCount++;
                loader.cache[url] = data;
                if(loader.isDone()){
                    loader.callback();
                }
            }
        };
        request.onerror = function(){
            loader.errorCount++;
            console.error(`Failed to load asset: { url: ${url}}`);
            if(loader.isDone()){
                loader.callback();
            }
        };
        request.send();
    }
}

//Loader.js
//Author - Arcxes

/*
    Loader is designed to load assets of all kinds of assets by passing the data to a specific loader and managing the results
*/

//imports
import {ImageLoader} from "./ImageLoader";
import {JSONLoader} from "./JSONLoader";

//Loader Class
export class Loader{

    constructor(){
        /**
         * @ignore
         * @type {number}
         */
        this.successCount = 0;
        /**
         * @ignore
         * @type {number}
         */
        this.errorCount = 0;
        /**
         * @ignore
         * @type {number}
         */
        this.totalAssets = 0;
        /**
         * @ignore
         * @type {Object}
         */
        this.cache = {};
        /**
         * @ignore
         * @type {string[]}
         */
        this.queue = [];
        /**
         * @ignore
         * @type {Function}
         */
        this.callback = null;
        /**
         * @ignore
         * @type {ImageLoader}
         */
        this.imageLoader = new ImageLoader();
        /**
         * @ignore
         * @type {JSONLoader}
         */
        this.jsonLoader = new JSONLoader(); 
    }

    /**
     * set the Loader callback
     * @param {Function} callback 
     */
    setCallback(callback){
        this.callback = callback;
    }

    /**
     * add an asset to be loaded
     * @param {string} url url to be queued
     */
    add(url){
        this.totalAssets++;
        this.queue.push(url);
    }

    /**
     * load an asset
     * @param {string} url url to be loaded
     */
    load(url){
        this.totalAssets++;
        let type = url.split(".");
        this.loadIntoMemory(url,type[1]);
    }

    /**
     * load all the queued assets
     * @param {Function} callback function to be called when assets are done loading
     */
    loadAssets(callback){
        if(callback != undefined){
            this.setCallback(callback);
        }
        if(this.isDone()){
            this.callback();
        }
        for(let i in this.queue){
            let url = this.queue[i];
            let type = url.split(".");
            this.loadIntoMemory(url,type[1]);
        }
    }

    /**
     * return the asset requested by its url
     * @param {string} url url of asset to retrieve
     * @return {*} asset that was retrieved
     */
    get(url){
        return this.cache[url];
    }

    /**
     * pass the url and type to a specific loader to be loaded into memory
     * @ignore
     * @param {string} url url to asset to load
     * @param {string} type type of asset to load
     */
    loadIntoMemory(url,type){
        if(type == "png" || type == "jpg" || type == "jpeg"){
            this.imageLoader.load(url,this);
        }else if(type == "mp3" || type == "wav" || type == "ogg"){
            this.audioLoader.load(url,this);
        }else if(type == "json"){
            this.jsonLoader.load(url,this);
        }else{
            console.error(`Failed to load asset: { url: ${url}}`);
        }
    }

    /**
     * check if all the assets are done loading
     * @ignore
     * @return {boolean} is the assets done loading
     */
    isDone(){
        if(this.totalAssets == this.successCount + this.errorCount){
            this.totalAssets = 0;
            this.queue = [];
            return true;
        }
        return false;
    }
}

