/*
    Client is designed to connect to a server, and send and receive packets
*/

//Client Class
export class Client{

    /**
     * 
     * @param {StringParser|JSONParser|BinaryParser} parser parser to use
     */
    constructor(parser){
        /**
         * @ignore
         * @type {WebSocket}
         */
        this.socket = null;
        /**
         * @ignore
         * @type {StringParser|JSONParser|BinaryParser}
         */
        this.parser = parser;
    }

    /**
     * connect to a web socket server
     * @param {string} url the url of the server
     */
    connect(url){
        this.socket = new WebSocket(url);
        this.parser.register(this.socket);
    }

    /**
     * send data to the server
     * @param {*} data data to send to the server
     */
    send(data){
        this.socket.send(this.parser.encode(data));
    }

    /**
     * parse data from the server
     * @param {*} data 
     * @return {*} data parsed into chosen parser format
     */
    parse(data){
        return this.parser.decode(data);
    }

    /**
     * set the onopen function of the websocket
     * @param {Function} callback onopen callback
     */
    set onopen(callback){
        this.socket.onopen = callback;
    }

    /**
     * set the onmessage function of the websocket
     * @param {Function} callback onmessage callback
     */
    set onmessage(callback){
        this.socket.onmessage = callback;
    }

    /**
     * set the onerror function of the websocket
     * @param {Function} callback onerror callback
     */
    set onerror(callback){
        this.socket.onerror = callback;
    }

    /**
     * set the onclose function of the websocket
     * @param {Function} callback onclose callback
     */
    set onclose(callback){
        this.socket.onclose = callback;
    }
}

//BinaryParser.js
//Author - Arcxes

/*
    BinaryParser is designed to convert json data to binary data and binary data to json data
*/

//BinaryParser Class
export class BinaryParser{

    constructor(){
        /**
         * @ignore
         * @type {number}
         */
        this.byteMultiplier = 2;
    }

    /**
     * set the data type for the socket
     * @param {WebSocket} socket websocket to be registed
     */
    register(socket){
        socket.binaryType = "arraybuffer";
        console.log(`WebSocket: ${socket} has been registered with BinaryParser`);
    }

    /**
     * encode data in binary format
     * @param {Object} data data to be encoded
     * @return {ArrayBuffer} buffer containing binary data
     */
    encode(data){
        let string = JSON.stringify(data);
        let buffer = new ArrayBuffer(string.length * this.byteMultiplier);
        let bufferView = new Uint16Array(buffer);
        let length = string.length;
        for(let i=0; i<length; i++){
            bufferView[i] = string.charCodeAt(i);
        }
        return buffer;
    }

    /**
     * decode the data
     * @param {*} data data to be decoded
     * @return {Object} json form of the data
     */
    decode(data){
        let string = String.fromCharCode.apply(null, new Uint16Array(data));
        let json = JSON.parse(string);
        return json;
    }
}

